import subprocess

def load_content_using_playwright(url):
    command = [
        "playwright", "codegen", url,
        "--output", "output.html"
    ]
    result = subprocess.run(command, capture_output=True, text=True)

    if result.returncode == 0:
        with open("output.html", "r", encoding="utf-8") as file:
            return file.read()
    else:
        raise Exception(f"Failed to load content using Playwright: {result.stderr}")
